mapper.update:
update shop
shop_name = #{name}, price = #{price}, description = #{description}, img = #{img}, status = #{status}, stock = #{stock},
where id = #{id}

mapper.select:

select
s.id, s.shop_name, s.description, s.create_time,
s.status, s.stock, s.img, s.price
from shop s
inner join shop_category sc on s.id = sc.shop_id
left join category c on c.id = sc.category_id



        and s.shop_name like concat('%', #{param.shop_name}, '%')



        and s.price <= #{param.price}


        and s.stock >= #{param.stock}


        and s.create_time >= #{param.start}


        and s.create_time <= #{param.end}



        and sc.category_id in

            #{cid} 




group by s.id


    having count(distinct sc.category_id) = ${param.categoryIds.size()}
